mapper.insert:
INSERT INTO t_board
    (iboard, bcode, iuser, title, content, group_idx, group_ord, group_dept)
VALUES
((SELECT NEXTVAL(iboard_seq))
,#{bcode}
,#{iuser}
,CONCAT('└ Re : ',#{title})
,#{content}
,#{group_idx}
,#{group_ord}+1
,#{group_dept}+1
)

mapper.update:
UPDATE t_board
SET group_ord = group_ord+1
WHERE group_idx = #{group_idx}
AND group_ord > #{group_ord}
